import HeaderAndNavBar from './HeaderAndNavBar';
import analyze from './static/search.png';
import {Link} from "react-router-dom";
import { useNavigate } from "react-router";
import { useState } from "react"
import axios from 'axios';
import './AddDream.css';

function AddDream(props) {
    let navigate = useNavigate();
    const [submitStatus, setSubmitStatus] = useState("Analyze Dream");
    const [dream, setDream] = useState({});

    const responseGenerate = async () => {

        // const res = await axios.get('http://localhost:5000/api');
    //     fetch("/api")
    //   .then((res) => res.json())
    //   .then((data) => {setDream((prevDream) => ({
    //         ...prevDream,
    //         dreamAnalysis: data.message // res.data.choices[0].message.content,
    //         // dreamImage: airespond.imageURL,
    //     }))});
        setSubmitStatus("Submit");


        // if (result.ok) {
        //   const airespond = await result.json(); // Parse the JSON response body
        //   setDream((prevDream) => ({
        //     ...prevDream,
        //     dreamAnalysis: airespond.analysis,
        //     // dreamImage: airespond.imageURL,
        // }));
        //   setSubmitStatus("Submit");
        // } else {
        //   setSubmitStatus("Retry");
        // }
      };


    async function handleSubmit(e) {
        e.preventDefault();
        setSubmitStatus("Waiting");
        const formData = new FormData(e.currentTarget);
        setDream((prevDream) => ({
            ...prevDream,
            dreamContent: formData.get("dreamDesc"),
        }));
        responseGenerate();
        props.setDreams(prevDreams => [dream, ...prevDreams]);
        // navigate('/Dreams', {state: {dream: formData.get("dreamDesc")}});
        // navigate('/Dreams');
    }

    return (
      <>
      <HeaderAndNavBar />
      <h1>Describe your dream:</h1>
      <form onSubmit={handleSubmit} className='dreamDescription'>
            <textarea name='dreamDesc' required={true} placeholder='I dreamed I was flying...'></textarea>
            <button class="analyzeButton"> 
            <img class="analyzeIcon" src={analyze} alt="analyze dream" />
            {submitStatus}
            </button>


      </form>
      <h1>{dream.dreamAnalysis}</h1>
      </>
    );
  }

  export default AddDream;
