import HeaderAndNavBar from './HeaderAndNavBar';
import analyze from './static/search.png';
import {Link} from "react-router-dom";
import { useNavigate } from "react-router";
import { useState } from "react"
import axios from 'axios';
import './AddDream.css';

function AddDream(props) {
    let navigate = useNavigate();
    const [submitStatus, setSubmitStatus] = useState("Analyze Dream");
    const [dream, setDream] = useState({});

    const responseGenerate = async (prompt) => {
        const result = await fetch("/api/ai", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({input: prompt})
          })

          if (result.ok) {
            const airespond = await result.json(); // Parse the JSON response body
            setDream((prevDream) => ({
                         ...prevDream,
                         dreamContent: prompt,
                         dreamAnalysis: airespond.result.analysis,
                         dreamGenre: airespond.result.genre,
                    }), () => {
                        props.setDreams(prevDreams => [{dream}, ...prevDreams])
                     })
            setSubmitStatus("Submit");
            props.setDreams(prevDreams => [{dreamContent: prompt,
                dreamAnalysis: airespond.result.ana, dreamGenre: airespond.result2}, ...prevDreams])
          } else {
            setSubmitStatus("Retry");
          }
      };

    async function handleSubmit(e) {
        e.preventDefault();
        setSubmitStatus("Waiting");
        const formData = new FormData(e.currentTarget);
        await responseGenerate(formData.get("dreamDesc"),);

        // navigate('/Dreams', {state: {dream: formData.get("dreamDesc")}});
        // navigate('/Dreams');
    }

    return (
      <>
      <HeaderAndNavBar />
      <h1>Describe your dream:</h1>
      <form onSubmit={handleSubmit} className='dreamDescription'>
            <textarea name='dreamDesc' required={true} placeholder='I dreamed I was flying...'></textarea>
            <button class="analyzeButton"> 
            <img class="analyzeIcon" src={analyze} alt="analyze dream" />
            {submitStatus}
            </button>
      </form>
      {dream.dreamAnalysis && <div id='AI'>{dream.dreamAnalysis}</div>}
      {dream.dreamGenre && <div id='AI'>{dream.dreamGenre}</div>}
      </>
    );
  }

  export default AddDream;
